package com.vroom_psshh.notesimport android.content.ContentValuesimport android.content.Contextimport android.database.Cursorimport android.database.DatabaseUtilsimport android.database.sqlite.SQLiteDatabaseimport android.database.sqlite.SQLiteOpenHelperclass NotesDbHelper(context: Context): SQLiteOpenHelper(context, DB_NAME, null, DB_VERSION) {    companion object {        private const val DB_NAME = "NotesDB"        private const val DB_VERSION = 1        private const val TABLE_NAME = "Users_Notes"        private const val COL1 = "Users_Entries"    }    override fun onCreate(db: SQLiteDatabase?) {        val table1 = "CREATE TABLE $TABLE_NAME (id INTEGER PRIMARY KEY, $COL1 TEXT)"        db?.execSQL(table1)    }    override fun onUpgrade(db: SQLiteDatabase?, oldVersion: Int, newVersion: Int) {        TODO("Not yet implemented")    }    fun addData(data: String){        val db: SQLiteDatabase = this.writableDatabase        val contentValues = ContentValues()        contentValues.put(COL1, data)        db.insert(TABLE_NAME, null, contentValues)    }    fun readAllData():Cursor{        val query = "SELECT * FROM $TABLE_NAME"        val db: SQLiteDatabase = this.readableDatabase        return db.rawQuery(query, null)    }    fun dbSize(): Long{        val db: SQLiteDatabase = this.readableDatabase        val dbSize = DatabaseUtils.queryNumEntries(db, TABLE_NAME)        db.close()        return dbSize    }}